import { Component } from '@angular/core';
import { Notes } from '../models/notes';

@Component({
  selector: 'app-home',
  templateUrl: 'home.page.html',
  styleUrls: ['home.page.scss'],
})
//document.getElementById('myTextarea').value = '';
export class HomePage {
  notesObject: Notes = {
    notesString: '',
    language: 'Deutsch',
  };

  constructor() {}

  storeLocalStoreNotes() {
    localStorage.setItem('inputNotes', this.notesObject.notesString);
  }

  getLocalStoreNotes() {
    this.notesObject.notesString = localStorage.getItem('inputNotes') || '';
  }
}
<ion-header [translucent]="true"> </ion-header>

<ion-content [fullscreen]="true">
  <ion-header collapse="condense">
    <ion-toolbar>
      <ion-title size="large">Blank</ion-title>
    </ion-toolbar>
  </ion-header>

  <ion-textarea
    id="notes-input"
    aria-label="Custom textarea"
    placeholder="What are you thinking about?"
    class="custom"
    [(ngModel)]="notesObject.notesString"
    Das
    ist
    nicht
    richtig
    der
    schreibt
    das
    dirket
    rein
    >{{notesObject.notesString}}</ion-textarea
  >

  <ion-fab id="organize-notes" vertical="bottom" horizontal="end" slot="fixed">
    <ion-fab-button>
      <ion-icon name="add"></ion-icon>
    </ion-fab-button>
  </ion-fab>
</ion-content>
